TRUNCATE TABLE DATA_NEWS;
INSERT INTO DATA_NEWS (`TITLE`, `DATE_ADD`, `TIME_ADD`, `TEXT`, `IMAGE`, `ON_INDEX`) VALUES 
	('Правила перевозки групп детей',	'2016.08.23',	'00:00:00',	'В ночное время суток (с 23:00 до 6:00) пассажирские перевозки групп детей допускаются лишь к железнодорожным вокзалам и аэропортам а так же в обратном направлении. В случае задержки в пути по причине поломки или пробок на дорогах разрешается перевозка детей до конечного пункта или пункта ночлега. Длинна маршрута после 23:00 не должно быть более 100 километров. При организованной междугородней перевозке длящейся более 3 часов необходимо сопровождения детей медицинским работником.',	'n1',	'1'), 
	('Схема маршрута',	'2016.07.14',	'00:00:00',	'По правилам транспортировки пассажиров водитель должен иметь схему маршрута проезда с указанием мест остановок. Заказчик может подготовит схему сам, проложив маршрут в Яндекс картах и распечатать затем его на бумажном носителе. На схеме маршрута должна стоять дата и подпись руководителя группы. Отсутствие схемы маршрута влечет наложение штрафа на перевозчика и задержки транспорта.',	'n2',	'1'), 
	('Список пассажиров автобуса',	'2016.06.11',	'00:00:00',	'Заказчик обязан составить поименный список пассажиров, желательно с номерами телефонов для предоставления его водителю автобуса. Список пассажиров составляется в трех экземплярах. В случае проверки автобуса транспортной инспекцией, водитель обязан предоставить список пассажиров. Отсутствие списка влечет наложение внушительного штрафа на владельца транспорта.',	'n3',	'1');

